const AboutUs = () => {
  return (
        <div className='aboutUsWrapper '>

          <section className='client_Section'>

            <div className='contentWrapper'>
              
              <h1> Our 100k talent mission: </h1>
              <p> To develop emerging minds and foster growth by eliminating inferiority complex and imposter syndrome within tech talents,end learning gaps and groom them to become world-class developers.</p>
            </div>

          </section>

          <section className='aboutUs_Section'>
            <div>
                <div className='titleSlash'>
                <h4>ABOUT US</h4><span></span>
                </div>
                 
                <div className='contentWrapper'>
                    <h5>We are Segsalerty Talent support an NGO platform set up for the purpose of developing, improving, and guiding tech talents</h5>
                    <p>We are Segsalerty Talent support an NGO platform set up for the purpose of developing, improving, and guiding tech talents, creating a platform for growth and development, and providing them with adequate resources to facilitate and empower them in their career path.</p>
                </div>
            </div>

            <div>
                <div className='titleSlash'>
                <h4>OUR APPROACH</h4><span></span>
                </div>
                <div className='contentWrapper'>
                    <h5> In todayâ€™s evolving business, technology leaders are looking for ways to stay ahead of customer expectations and accelerate innovation. </h5>
                    <p> Investing in the professional development of talents is a valuable strategy, that benefits both employees and organizations as a whole and that is where Segsalerty Talent Support comes into place. To address these realities by producing an updated curriculum and supporting resources, that will encourage and foster the learning and development process of tech talents, preparing them with the right resources and facilities to groom them for bigger exploration in the world of technology</p>
                    <p>At the end of each cohort we hope to produce quality and multifaceted individuals that will set a standard globally and work at developing great innovations in the world at large.</p>
                </div>
            </div>


          </section>

          <section className='whatWeDoSection section_with_subSections'>

            {/* <h5 className='sectionLabel'>{programSection.label}</h5> */}

            <div className='whatWeDo should_Flex'>

              <div className='contentWrapper'>
                <h4>What we do</h4>
                <p>We believe in harnessing the skill and capacity of talented individuals and grooming them to be their better selves through self and technical developmental processes</p>
                <div className='listingWrapper'>

                  <div>
                    <span>{establish}</span>
                    <p>Establish a forum and facility to improve learning.</p>
                  </div>

                  <div>
                    <span>{aid}</span>
                    <p>Aid and foster growth while promoting diversity and inclusion in the industry</p>
                  </div>

                  <div>
                    <span>{groom}</span>
                    <p>Groom talents to become world-class techies.</p>
                  </div>

                </div>
              </div>

              <div className='imageWrapper'>
                <img src={whatWeDoImg} alt="img" />
              </div>

            </div>

            <div className='vision should_Flex'>

              <div className='contentWrapper'>
                <h4>Vision Statement</h4>
                <p>A global hub where tech professionals are appreciated, valued and groomed to be their very best and not limited by demographic segmentation.</p>
                <div className='value'>
                  <h6>Value proposition</h6>
                  <div className='listingWrapper'>
                    <div>
                      <span>{growth}</span>
                      <p>Individual growth</p>
                    </div>

                    <div>
                      <span>{skills}</span>
                      <p>Techinical skills</p>
                    </div>

                    <div>
                      <span>{visibility}</span>
                      <p>Visibility</p>
                    </div>

                  </div>
                </div>
              </div>

              <div className='imageWrapper'>
                <img src={visionStatementImg} alt="img" />
              </div>

            </div>


          </section>
          
          <Faq />
          <FooterBanner />

        </div>
  );
}


let SkillCard = ({image,title,des}) => {
  return (
    <div className='card'>
        <img src={image} alt="" />
        <div>
        <h5>{title}</h5>
        <p>{des}</p>
        </div>
    </div>
  )
}

export default AboutUs;
